mapper.select:
SELECT m.movieCode, m.movieNm, audits, thumbNail, movieStory, TO_CHAR(startDate,'YYYY.MM.DD') startDate, TO_CHAR(endDate, 'YYYY.MM.DD') endDate, movieScores
	FROM movie m left outer join (select movieCode, NVL(AVG(movieScores)*2,0) movieScores from movieScore group by movieCode) s on m.movieCode= s.movieCode
	WHERE m.movieCode=#{movieCode}
